using NUnit.Framework;
using ProgrammersSpot.Business.Models.Users;

namespace ProgrammersSpot.Business.Models.Tests.UserTests
{
    [TestFixture]
    public class UserTests
    {
        [TestCase("data:image/jpeg;base64,")]
        [TestCase("https://images.pexels.com/photos/36753/flower-purple-lical-blosso.jpg?w=940&h=650&auto=compress&cs=tinysrgb")]
        public void AvatarUrl_ShouldBeSetCorrectly(string url)
        {
            // Arrange & Act
            var user = new User() { AvatarUrl = url };

            //Assert
            Assert.AreEqual(user.AvatarUrl, url);
        }

        [TestCase(true)]
        [TestCase(false)]
        public void IsDeleted_ShouldBeSetCorrectly(bool isDeleted)
        {
            // Arrange & Act
            var user = new User { IsDeleted = isDeleted };

            //Assert
            Assert.AreEqual(user.IsDeleted, isDeleted);
        }
    }
}
